import { useState } from "react";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css"

function Login() {
    const [email,setEmail] = useState("");
    var [emailError ,setEmailError]=useState("");
    const [password,setPassword]=useState("");
    var [passwordError,setPasswordError]= useState("");   

    const url = "http://127.0.0.1:9999/login";


    const OnTextChange= (args)=>{
            setEmail(args.target.value);

    } ;

    const OnTextChangePassword =(args)=>{
        setPassword(args.target.value);
    };
    const Validate=()=>{
        //initially set to empty 
        setEmailError("");
        setPasswordError("");

        if(email===""){
                setEmailError("Please Enter Your Email")
                return;
        }
        if (!/^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/.test(email)) {
            setEmailError("Please enter a valid email")
            return
        }
        if(""===password)
        {
            setPasswordError("Please Enter your Password");
            return ;
        }
        if(password.length<8){
            setPasswordError("Password must be  8 characters or Longer");
            return;
        }

        //validating user 


    };

    return (
        <div className="container">
            <center>
                <div className="container">
                    <h1 className="text-primary"> LogIn </h1>
                    <input
                value={email}
                onChange={OnTextChange}
                placeholder="Enter your email here"
                name= "email" />
            <label className="errorLabel">{emailError}</label>
            <br/>
            <br/>
            <input
                type="password"
                value={password}
                onChange={OnTextChangePassword}
                placeholder="Enter your password here"
                name= "password" />
                <label className="errorLabel">{passwordError}</label>
                <br/>
                <br/>
                <input  className="button button-primary" type="button" onClick={Validate} value={"Log In"} />



                </div>

            </center>
        </div>

     );
}

export default Login;
