import { useState, useEffect } from "react";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css"
import axios from 'axios';


function Register() {

    var [firstNameError, setFirstNameError] = useState("")
    var [lastNameError, setLastNameError] = useState("")
    var [emailError, setEmailError] = useState("");
    var [mobileNoError, setMobileNoError] = useState("");
    var [passwordError, setPasswordError] = useState("");
    var [userTypeError, setUserTypeError] = useState("");
    var[message, setMessage]=useState(" ");

    const [user, setUser] = useState({ firstName: " ", lastName: " ", email: " ", password: "", mobileNo: "", userType: " " });

    const url = "http://127.0.0.1:9999/register";

    const OnTextChage = (args) => {
        debugger
        var copyOfUser = { ...user };
        copyOfUser[args.target.name] = args.target.value;
        setUser(copyOfUser);
    }

    const onSelectChange =(args) =>{
        debugger
        var copyOfUser = { ...user };
        copyOfUser[args.target.name] = args.target.value;
        setUser(copyOfUser);
    }

    const AddUser=()=>{
        debugger
        if(Validate){
            axios.post(url,user).then((result)=>{
                if(result.data.affectedrows!==undefined && result.data.affectedrows>0){
                    showMessage("Your Registration Is Successfull")
                    reset();
                }
            });

        }   
        else
        {
            return ;
        }  
    }

    const reset =()=>{
        setUser({firstName: " ", lastName: " ", email: " ", password: "", mobileNo: "", userType: " " })
    }

    const showMessage =(msgtext)=>{
        setMessage(msgtext);
        window.setTimeout(() => {
            setMessage("");
        }, 5000)
    }
    
    const Validate = () => {
        debugger;
        //initially set to empty 
        setFirstNameError("");
        setLastNameError("");
        setEmailError("");
        setPasswordError("");
        setMobileNoError(" ");
        setUserTypeError("");

        if (user.firstName === "") {
            setFirstNameError("*Please Enter Your FirstName")
            
        }
        if (user.lastName === "") {
            setLastNameError("*Please Enter Your LastName")

        }
        if (user.email === "") {
            setEmailError("*Please Enter Your Email")

        }
        if (!/^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/.test(user.email)) {
            setEmailError("*Please enter a valid email")

        }
        if ("" === user.password) {
            setPasswordError("*Please Enter your Password");
        }
        if (user.password.length < 8) {
            setPasswordError("*Password must be  8 characters");

        }
        if (user.mobileNo === "") {
            setMobileNoError("*Please Enter Your MobileNo");

        }
        if (user.mobileNo.length=10) {
            setMobileNoError("*MobileNo must be 10 characters");
        }
        if(user.userType===" "){
            setUserTypeError("*Please Enter Your UserType");
        }
        if( firstNameError=="" || lastNameError=="" || emailError=="" ||
            mobileNoError=="" ||passwordError=="" ||userTypeError==""){
                return true;
        }
        else
            return false;
     
    };


    return (
        <div className="container">
            <center>
                <br />
                <br />
                <br />
                <br />
                    <h1 className="text-primary"> REGISTER NEW USER </h1>
                   
                        <div className="table-responsive">
                            <table className="table">
                                <tbody>
                                    <tr>
                                        <td>First Name:</td>
                                        <td><input type="text" name="firstName" value={user.firstName} onChange={OnTextChage} /> </td>
                                        <td className="text-danger" value={firstNameError}></td>
                                    </tr>
                                    <tr>
                                        <td>Last Name:</td>
                                        <td> <input type="text"  name="lastName" value={user.lastName} onChange={OnTextChage} /> </td>
                                        <td className="text-danger">{lastNameError}</td>
                                    </tr>
                                    <tr>
                                        <td> Email: </td>
                                        <td><input type="email" name="email" value={user.email} onChange={OnTextChage} /></td>
                                        <td  className="text-danger" >{emailError}</td>
                                    </tr>
                                    <tr>
                                        <td>Password:</td>
                                        <td> <input type="password" name="password" value={user.password} onChange={OnTextChage} /> </td>
                                        <td className="text-danger"> {passwordError} </td>
                                    </tr>
                                    <tr>
                                        <td>Mobile Number:</td>
                                        <td><input type="tel"  name ="mobileNo" value={user.mobileNo} onChange={OnTextChage} /> </td>
                                        <td className="text-danger">{mobileNoError} </td>
                                    </tr>
                                    <tr>
                                        <td>UserType:</td>
                                        <td><select value={user.userType} name="userType" onChange={OnTextChage} >  
                                            <option value="0">Owner</option>
                                            <option value="1">Customer</option>
                                        </select>
                                        </td>
                                        <td className="text-danger">{userTypeError} </td>
                                    </tr>
                                </tbody>
                            </table>
                        </div>
                        
                        <button  className="btn btn-primary" onClick={AddUser} > Register</button>
                    
                    <div >
                        {message}
                    </div>
            </center>
        </div>
    );
}

export default Register;
